import axios from "axios";

const API_BASE = "http://127.0.0.1:5000";

export const api = axios.create({
  baseURL: API_BASE,
  headers: {
    "Content-Type": "application/json",
  },
});

// tasks
export const createTask = (title) => api.post("/api/tasks", { title });
export const listTasks = () => api.get("/api/tasks");

// comments
export const listComments = (taskId) => api.get(`/api/tasks/${taskId}/comments`);
export const createComment = (taskId, payload) =>
  api.post(`/api/tasks/${taskId}/comments`, payload);
export const updateComment = (taskId, commentId, payload) =>
  api.put(`/api/tasks/${taskId}/comments/${commentId}`, payload);
export const deleteComment = (taskId, commentId) =>
  api.delete(`/api/tasks/${taskId}/comments/${commentId}`);
