import React, {useEffect, useState} from "react";
import "./App.css";
import {
  listTasks,
  createTask,
  listComments,
  createComment,
  deleteComment
} from "./api";

function Sidebar({ tasks, selectedId, onSelect, onCreate }) {
  const [title, setTitle] = useState("");
  return (
    <div className="card sidebar">
      <h2>Tasks</h2>
      <div className="controls">
        <input className="input" placeholder="New task title" value={title} onChange={e=>setTitle(e.target.value)} />
        <button className="btn" onClick={async ()=>{
          if(!title.trim()) return alert("Enter a title");
          await onCreate(title.trim());
          setTitle("");
        }}>Add</button>
      </div>
      <div>
        {tasks.length === 0 && <div className="empty">No tasks yet</div>}
        {tasks.map(t=>(
          <div key={t.id} className={`task-item ${selectedId===t.id ? "selected":""}`} onClick={()=>onSelect(t.id)}>
            <div><strong>{t.title}</strong></div>
            <div className="small">{t.description || <em>No description</em>}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

function Main({ taskId }) {
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [content, setContent] = useState("");
  useEffect(()=> {
    if (!taskId) return setComments([]);
    setLoading(true);
    listComments(taskId).then(r=>{
      setComments(r.data || []);
    }).catch(()=> setComments([])).finally(()=> setLoading(false));
  }, [taskId]);

  async function handleAdd(){
    if(!content.trim()) return alert("Write something");
    await createComment(taskId, { content: content.trim(), author: "Zaid" });
    setContent("");
    const r = await listComments(taskId);
    setComments(r.data || []);
  }

  async function handleDelete(cid){
    if(!window.confirm("Delete this comment?")) return;
    await deleteComment(taskId, cid);
    setComments(prev => prev.filter(c=> c.id !== cid));
  }

  if(!taskId) return <div className="card main"><div className="empty">Select a task to view comments</div></div>

  return (
    <div className="card main">
      <h2>Comments (Task #{taskId})</h2>
      <div style={{marginBottom:12}}>
        <textarea className="input" style={{width:"100%", minHeight:80}} value={content} placeholder="Write a comment..." onChange={e=>setContent(e.target.value)} />
        <div style={{display:"flex", justifyContent:"flex-end", gap:8, marginTop:8}}>
          <button className="btn" onClick={handleAdd}>Add Comment</button>
        </div>
      </div>

      <div>
        {loading && <div className="small">Loading commentsâ€¦</div>}
        {!loading && comments.length === 0 && <div className="empty">No comments yet</div>}
        {comments.map(c => (
          <div className="comment" key={c.id}>
            <div>
              <div><strong>{c.author || "Anon"}</strong></div>
              <div className="meta">{c.content}</div>
            </div>
            <div style={{textAlign:"right"}}>
              <div className="small">{new Date(c.created_at || Date.now()).toLocaleString()}</div>
              <div style={{marginTop:8}}>
                <button className="btn ghost" onClick={()=>handleDelete(c.id)}>Delete</button>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className="footer">Note: This demo uses a local Flask backend (port 5000).</div>
    </div>
  );
}

export default function App(){
  const [tasks, setTasks] = useState([]);
  const [selected, setSelected] = useState(null);

  useEffect(()=> {
    refresh();
  }, []);

  async function refresh(){
    try{
      const r = await listTasks();
      setTasks(r.data || []);
      if(r.data && r.data.length && !selected) setSelected(r.data[0].id);
    }catch(e){
      console.error(e);
      setTasks([]);
    }
  }

  async function handleCreateTask(title){
    await createTask(title);
    await refresh();
  }

  return (
    <div className="app">
      <Sidebar tasks={tasks} selectedId={selected} onSelect={setSelected} onCreate={handleCreateTask} />
      <Main taskId={selected} />
    </div>
  )
}
